import { useState } from "react";
import {
  Box,
  Typography,
  FilledInput,
  IconButton,
  useTheme,
} from "@material-ui/core";
import {
  Search as SearchIcon,
  Bookmark as BookmarkIcon,
} from "@material-ui/icons";
import { useHistory, Link } from "react-router-dom";

const Home = () => {
  const [word, setWord] = useState("");
  const theme = useTheme();
  const history = useHistory();

  const handleSubmit = (event) => {
    event.preventDefault();
    const trimmedWord = word.trim().toLowerCase();
    if (!trimmedWord || trimmedWord.split(" ").length > 1) return;
    history.push(`/search/${trimmedWord}`);
  };
  return (
    <Box sx={{ ...theme.mixins.alignInTheCenter }}>
      <img src="/assets/book.png" alt="Book" />
      <Typography
        color="primary"
        sx={{
          mt: 3,
          mb: 1,
        }}
        variant="h4"
      >
        Hans Wehr
      </Typography>
      <Typography color="GrayText">Find meanings of arabic roots</Typography>
      <Box sx={{ width: "360px" }}>
        <form onSubmit={handleSubmit}>
          <FilledInput
            value={word}
            onChange={(event) => setWord(event.target.value)}
            disableUnderline
            placeholder="Search for a root"
            sx={{
              my: 4,
              backgroundColor: "white",
              borderRadius: 2,
              boxShadow: "0px 10px 25px rgba(0, 0, 0, 0.05)",
              "& .MuiFilledInput-input": {
                p: 2,
              },
            }}
            startAdornment={<SearchIcon color="disabled" />}
            fullWidth
          />
        </form>
      </Box>
      <IconButton
        to="/bookmarks"
        component={Link}
        sx={{
          borderRadius: 2,
          p: 2,
          color: "#fff",
          background: (theme) => theme.palette.pink,
          boxShadow: "0px 10px 10px rgba(221, 114, 133, 0.2)",
        }}
      >
        <BookmarkIcon />
      </IconButton>
    </Box>
  );
};

export default Home;
